class MainActivity : ComponentActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()

        setContent {
            NextBusTheme {
                Scaffold(modifier = Modifier.fillMaxSize()) { paddingValues ->
                    MainApp(activity = this@MainActivity, modifier = Modifier.padding(paddingValues))
                }
            }
        }
    }
}

@Composable
fun MainApp(activity: MainActivity, modifier: Modifier = Modifier) {
    var hasLocationPermission by remember { mutableStateOf(false) }
    var appInitialized by remember { mutableStateOf(false) }
    var selectedScheduleId by remember { mutableStateOf<Long?>(null) }

    // Initialize location manager and repository
    val fusedLocationProviderClient = remember {
        LocationServices.getFusedLocationProviderClient(activity)
    }
    val locationManager = remember {
        LocationManager(activity, fusedLocationProviderClient)
    }

    val database = remember { AppDatabase.getInstance(activity) }
    val repository = remember { BusScheduleRepository(database.busScheduleDao()) }
    val viewModel: BusScheduleViewModel = viewModel(
        factory = object : androidx.lifecycle.ViewModelProvider.Factory {
            override fun <T : androidx.lifecycle.ViewModel> create(modelClass: Class<T>): T {
                @Suppress("UNCHECKED_CAST")
                return BusScheduleViewModel(repository) as T
            }
        }
    )

    val schedules by viewModel.schedules.collectAsState()
    val location by locationManager.locationState
    val scope = rememberCoroutineScope()

    val locationPermissionLauncher = rememberLauncherForActivityResult(
        contract = ActivityResultContracts.RequestPermission()
    ) { isGranted ->
        hasLocationPermission = isGranted
        if (isGranted) {
            locationManager.startLocationUpdates()
            scope.launch {
                locationManager.updateAddressFromLocation()
            }
        }
        appInitialized = true
    }

    LaunchedEffect(Unit) {
        val permission = Manifest.permission.ACCESS_FINE_LOCATION

        hasLocationPermission = ContextCompat.checkSelfPermission(
            activity,
            permission
        ) == PackageManager.PERMISSION_GRANTED

        if (!hasLocationPermission) {
            locationPermissionLauncher.launch(permission)
        } else {
            locationManager.startLocationUpdates()
            scope.launch {
                locationManager.updateAddressFromLocation()
            }
            appInitialized = true
        }
    }

    Box(modifier = modifier) {
        if (appInitialized) {
            val selectedSchedule = schedules.find { it.id == selectedScheduleId }

            if (selectedSchedule != null) {
                BusScheduleDetailScreen(
                    schedule = selectedSchedule,
                    onBack = { selectedScheduleId = null }
                )
            } else {
                BusScheduleListScreen(
                    schedules = schedules,
                    location = location,
                    onAddSchedule = { timestamp, route, place, seating, latitude, longitude, address, busType, busTier, busRating ->
                        viewModel.addSchedule(
                            timestamp, route, place, seating,
                            latitude, longitude, address,
                            busType, busTier, busRating
                        )
                    },
                    onDeleteSchedule = { schedule ->
                        viewModel.deleteSchedule(schedule)
                    },
                    onSelectSchedule = { schedule ->
                        selectedScheduleId = schedule.id
                    }
                )
            }
        }
    }
}

package com.sameerasw.nextbus.data

import android.content.Context
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase

@Database(entities = [BusScheduleEntity::class], version = 1)
abstract class AppDatabase : RoomDatabase() {
    abstract fun busScheduleDao(): BusScheduleDao

    companion object {
        @Volatile
        private var instance: AppDatabase? = null

        fun getInstance(context: Context): AppDatabase {
            return instance ?: synchronized(this) {
                instance ?: Room.databaseBuilder(
                    context.applicationContext,
                    AppDatabase::class.java,
                    "bus_schedule_db"
                ).build().also { instance = it }
            }
        }
    }
}

package com.sameerasw.nextbus.data

import androidx.room.Dao
import androidx.room.Delete
import androidx.room.Insert
import androidx.room.Query
import kotlinx.coroutines.flow.Flow

@Dao
interface BusScheduleDao {
    @Query("SELECT * FROM bus_schedule ORDER BY timestamp DESC")
    fun getAllSchedules(): Flow<List<BusScheduleEntity>>

    @Query("SELECT * FROM bus_schedule ORDER BY timestamp DESC")
    suspend fun getAll(): List<BusScheduleEntity>

    @Query("SELECT * FROM bus_schedule WHERE id = :id")
    suspend fun getById(id: Long): BusScheduleEntity?

    @Insert
    suspend fun insert(schedule: BusScheduleEntity): Long

    @Delete
    suspend fun delete(schedule: BusScheduleEntity)

    @Query("DELETE FROM bus_schedule WHERE id = :id")
    suspend fun deleteById(id: Long)
}

package com.sameerasw.nextbus.data

import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flow
import kotlinx.coroutines.flow.flowOn

class BusScheduleRepository(private val dao: BusScheduleDao) {
    fun getAllSchedules(): Flow<List<BusScheduleEntity>> = dao.getAllSchedules()

    suspend fun insertSchedule(schedule: BusScheduleEntity): Long {
        return dao.insert(schedule)
    }

    suspend fun deleteSchedule(schedule: BusScheduleEntity) {
        dao.delete(schedule)
    }

    suspend fun deleteScheduleById(id: Long) {
        dao.deleteById(id)
    }

    suspend fun getScheduleById(id: Long): BusScheduleEntity? {
        return dao.getById(id)
    }
}

package com.sameerasw.nextbus.location

import android.annotation.SuppressLint
import android.content.Context
import android.location.Address
import android.location.Geocoder
import android.os.Looper
import androidx.compose.runtime.State
import androidx.compose.runtime.mutableStateOf
import com.google.android.gms.location.FusedLocationProviderClient
import com.google.android.gms.location.LocationCallback
import com.google.android.gms.location.LocationRequest
import com.google.android.gms.location.LocationResult
import com.google.android.gms.location.Priority
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import java.util.Locale

data class LocationData(
    val latitude: Double? = null,
    val longitude: Double? = null,
    val address: String? = null
)

class LocationManager(
    private val context: Context,
    private val fusedLocationProviderClient: FusedLocationProviderClient
) {
    private val _locationState = mutableStateOf(LocationData())
    val locationState: State<LocationData> = _locationState

    private var locationCallback: LocationCallback? = null
    private val geocoder = Geocoder(context, Locale.getDefault())

    @SuppressLint("MissingPermission")
    fun startLocationUpdates() {
        val locationRequest = LocationRequest.Builder(
            Priority.PRIORITY_HIGH_ACCURACY,
            5000L // 5 seconds
        ).build()

        locationCallback = object : LocationCallback() {
            override fun onLocationResult(locationResult: LocationResult) {
                super.onLocationResult(locationResult)
                locationResult.lastLocation?.let { location ->
                    _locationState.value = _locationState.value.copy(
                        latitude = location.latitude,
                        longitude = location.longitude
                    )
                }
            }
        }

        fusedLocationProviderClient.requestLocationUpdates(
            locationRequest,
            locationCallback!!,
            Looper.getMainLooper()
        )
    }

    fun stopLocationUpdates() {
        locationCallback?.let {
            fusedLocationProviderClient.removeLocationUpdates(it)
        }
    }

    suspend fun reverseGeocode(latitude: Double, longitude: Double): String? {
        return withContext(Dispatchers.IO) {
            try {
                @Suppress("DEPRECATION")
                val addresses: List<Address>? = geocoder.getFromLocation(latitude, longitude, 1)
                if (!addresses.isNullOrEmpty()) {
                    val address = addresses[0]
                    buildAddressString(address)
                } else {
                    null
                }
            } catch (e: Exception) {
                null
            }
        }
    }

    private fun buildAddressString(address: Address): String {
        return listOfNotNull(
            address.thoroughfare,
            address.locality,
            address.adminArea,
            address.countryName
        ).joinToString(", ")
    }

    suspend fun updateAddressFromLocation() {
        val lat = _locationState.value.latitude
        val lng = _locationState.value.longitude
        if (lat != null && lng != null) {
            val address = reverseGeocode(lat, lng)
            _locationState.value = _locationState.value.copy(address = address)
        }
    }

    fun getCurrentLocation(): LocationData = _locationState.value
}

package com.sameerasw.nextbus.ui
import androidx.lifecycle.ViewModel import androidx.lifecycle.viewModelScope import com.sameerasw.nextbus.data.BusEmbedded import com.sameerasw.nextbus.data.BusScheduleEntity import com.sameerasw.nextbus.data.BusScheduleRepository import kotlinx.coroutines.flow.SharingStarted import kotlinx.coroutines.flow.StateFlow import kotlinx.coroutines.flow.stateIn import kotlinx.coroutines.launch
class BusScheduleViewModel(private val repository: BusScheduleRepository) : ViewModel() { val schedules: StateFlow<List<BusScheduleEntity>> = repository.getAllSchedules() .stateIn(viewModelScope, SharingStarted.WhileSubscribed(5000), emptyList())
fun addSchedule( timestamp: Long, route: String, place: String, seating: String?, latitude: Double?, longitude: Double?, address: String?, busType: String?, busTier: String?, busRating: Double? ) { viewModelScope.launch { val schedule = BusScheduleEntity( timestamp = timestamp, route = route, place = place, seating = seating, locationLat = latitude, locationLng = longitude, locationAddress = address, bus = BusEmbedded( type = busType, tier = busTier, rating = busRating ) ) repository.insertSchedule(schedule) } }
fun deleteSchedule(schedule: BusScheduleEntity) { viewModelScope.launch { repository.deleteSchedule(schedule) } } }

