val popularRoutes = listOf(
    "Colombo  Kandy",
    "Colombo  Galle",
    "Colombo  Jaffna",
    "Kandy  Colombo",
    "Galle  Colombo",
    "Colombo  Matara",
    "Kandy  Galle",
    "Colombo  Anuradhapura",
    "Colombo  Nuwara Eliya",
    "Colombo  Negombo",
    "Colombo  Batticaloa",
    "Colombo  Trincomalee",
    "Colombo  Kurunegala",
    "Colombo  Ratnapura",
    "Colombo  Matara",
    "Kandy  Nuwara Eliya"
)

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun RouteSearchScreen(
    onSelectRoute: (String) -> Unit,
    onBack: () -> Unit
) {
    var searchQuery by remember { mutableStateOf("") }

    val filteredRoutes = if (searchQuery.isEmpty()) {
        popularRoutes
    } else {
        popularRoutes.filter { it.contains(searchQuery, ignoreCase = true) }
    }

    Scaffold(
        topBar = {
            CenterAlignedTopAppBar(
                title = { Text("Select Route") },
                navigationIcon = {
                    IconButton(onClick = onBack) {
                        Icon(Icons.Default.ArrowBack, contentDescription = "Back")
                    }
                }
            )
        }
    ) { innerPadding ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(innerPadding)
        ) {
            TextField(
                value = searchQuery,
                onValueChange = { searchQuery = it },
                placeholder = { Text("Search routes...") },
                leadingIcon = { Icon(Icons.Default.Search, contentDescription = null) },
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(8.dp),
                singleLine = true
            )

            LazyColumn(modifier = Modifier.fillMaxSize()) {
                items(filteredRoutes) { route ->
                    ListItem(
                        headlineContent = { Text(route) },
                        modifier = Modifier.clickable {
                            onSelectRoute(route)
                            onBack()
                        }
                    )
                }
            }
        }
    }
}

